<template>
	<view class="container">
		<!-- 顶部菜单 -->
		<view class="top-menu">
			<view v-for="(item, index) in menus" :key="index"
				:class="['menu-item', activeMenu === index ? 'active' : '']" @click="changeMenu(index,item.status)">
				{{ item.name }}
				<span v-if="activeMenu === index"></span>
			</view>
		</view>

		<!-- 筛选按钮 -->
		<view class="filter-btns">
			<view v-for="(btn, index) in filters" :key="index"
				:class="['filter-btn', activeFilter === index ? 'active' : '']" @click="changeFilter(btn,index)">
				{{ btn }}
			</view>
		</view>

		<!-- 保险箱入口 -->
    <view class="safe-box-btn" @click="navigateToSafeBox">
	  <uni-icons type="locked" size="20"></uni-icons>
      <text class="count">{{ lockProductsLength }}</text>
    </view>

		<!-- 发货提示 -->
		<view class="shipping-tips"></view>

		<!-- 产品列表 -->
		<scroll-view class="product-list" scroll-y>
			<view v-for="(item, index) in filteredProducts" :key="item.productId" class="product-info">
				<view class="product-item" @tap="toDetailPage(item)">
					<image class="product-img" :src="item.productImage" lazy-load/>

					<view class="product-info">
						<view class="top-section">
							<text class="name">{{ item.productName }}</text>
						</view>
						<view class="meta-info">
							<text class="source">{{ item.activityType }}</text>
							<text class="grade">{{ item.productLevel }}</text>
						</view>
						<view class="medal-info">
							<text>可抵扣{{ item.productBadge }}勋章</text>
							<view class="status-btn">{{ showStatusWord(item) }}</view>
						</view>

					</view>
				</view>
				<view class="bottom-section" :class="!showShipStatus || activeMenu !== 0 ? 'bottom-section-position' : ''">
					<view class="check-product" v-if="showShipStatus && activeMenu === 0">
						<checkbox-group @change="handleChange($event,item)">
							<label>
								<checkbox :value="item.checked" :checked="item.checked" />选择
							</label>
						</checkbox-group>
					</view>
					<view class="right-bottom-wrapper" v-if="!showShipStatus ||  activeMenu !== 0">
						<view class="lock-icon" v-if="item.status === 'pending'" @tap="lockProduct(item)"></view>
						<view class="donate-btn" v-if="item.status === 'shipped'">查看订单</view>
						<view class="donate-btn pink-btn" v-if="item.status === 'pedding'">赠送</view>
						<view class="donate-btn pink-btn" v-if="item.status === 'locked'" @tap="unLockProduct(item)">解锁</view>
					</view>
					
				</view>
			</view>
		</scroll-view>

		<!-- 底部操作 -->
		<view class="bottom-action" v-if="!showShipStatus && activeMenu === 0">
			<view class="ship-btn defaut-btn" :class="{disabled: selectedCount === 0}" @click="handleShip"></view>
			<view class="defaut-btn market-btn" @click="navigateToMarket()"></view>
		</view>
		<view class="bottom-action-select" v-if="showShipStatus &&  activeMenu === 0">
			<view class="default-btn" @tap="checkAll">
				全选
			</view>
			<view class="default-btn" @tap="handleCancel">
				取消
			</view>
			<view class="default-btn select-product">
				提交发货(已选择{{selectedCount}}件)
			</view>
		</view>
	</view>
</template>

<script>
	import {
		get,
		post
	} from "@/utils/rest-util.js"
	export default {
		onLoad() {
			this.loadPageData('pending');
			this.loadLockData()
		},
		data() {
			return {
				menus: [{name:'待处理',status:'pending'},{name:'已提货',status:'shipped'},{name:'全部',status:'all'}],
				filters: ['全部', 'A赏', 'B赏', '终赏', '其他'],
				activeMenu: 0,
				activeFilter: 0,
				showShipStatus: false,
				allProduct: [],
				filteredProducts: [],
				lockProductsLength: 0
			}
		},
		computed: {
			selectedCount() {
				return this.filteredProducts.filter(item => item.checked).length
			},
			
		},
		methods: {
			showStatusWord(item) {
				let str = '待处理';
				if (item.status === 'shipped') {
					str = '已提货';
				}
				if (item.status === 'locked') {
					str = '锁定中';
				}
				return str
			},
			fiterProduct(level) {
				if(level === '其他'){
					this.filteredProducts =  this.allProduct.filter(item=> !['A赏','B赏','终赏'].some(obj=>obj === item.productLevel))
					return
				}
				if(level === 'all'){
					this.filteredProducts = this.allProduct;
				} else {
					this.filteredProducts = this.allProduct.filter(item=>item.productLevel == level)
				}
				
			},
			lockProduct(item){
				
			},
			unLockProduct(item){
				
			},
			handleCancel() {
				this.filteredProducts = this.filteredProducts.map(item => {
					item.checked = false
					return item
				})
				this.showShipStatus = false;
			},
			checkAll() {
				this.filteredProducts = this.filteredProducts.map(item => {
					item.checked = true
					return item
				})
			},
			handleChange(value,item) {
				const {detail} = value;
				const flag = detail.value[0] || false;
				item.checked = flag;
			},
			changeMenu(index,status) {
				this.activeMenu = index;
				this.loadPageData(status)
			},
			changeFilter(item,index) {
				this.activeFilter = index;
				const type = index === 0 ? 'all' : item;
				this.fiterProduct(type)
			},
			navigateToSafeBox() {
				uni.navigateTo({
					url: '/pages/safe/index'
				})
			},
			navigateToMarket() {
				uni.navigateTo({
					url: '/pages/market/index'
				})
			},
			handleShip() {
				const selectCount = this.filteredProducts.filter(item => item.checked)
				this.showShipStatus = !this.showShipStatus;
				// 处理发货逻辑
			},
			toDetailPage(item){
				uni.navigateTo({
					url: '/pages/box/detail'
				})
			},
			loadPageData(status){
				get('wx/boxproduct/getProductsByUser?userId=U10001&status='+status).then(json => {
					const result = json.data.data;
					this.allProduct = result.allProducts.map(item=>{
						return {
							...item,
							checked:false
						}
					});
					this.fiterProduct('all')
				})
			},
			loadLockData(){
				get('wx/boxproduct/getProductsByUser?userId=U10001&status=locked').then(json => {
					const result = json.data.data;
					this.lockProductsLength = result.allProducts?.length || 0;
					console.log(this.lockProductsLength)
				})
			}
		}
	}
</script>

<style lang="scss" scoped>
	.container {
		padding-bottom: 120rpx;
		min-height: 90vh;
		/* 关键样式 */
		background-size: cover;
		/* 或使用 contain 根据需求 */
		background-position: center;
		background-repeat: no-repeat;
		background-image: url('data:image/jpeg;base64,');
	}

	.top-menu {
		display: flex;
		padding: 10rpx 30rpx;

		.menu-item {
			flex: 1;
			text-align: center;
			font-size: 32rpx;
			padding: 20rpx 0;

			&.active {
				position: relative;

				span {
					display: inline-block;
					position: absolute;
					width: 120rpx;
					height: 70rpx;
					background-image: url('~@/static/circe.png');
					background-size: cover;
					/* 或使用 contain 根据需求 */
					background-position: 100% 100%;
					background-repeat: no-repeat;
					top: 5rpx;
					left: 60rpx;
					z-index: 2;
				}
			}
		}
	}

	.filter-btns {
		display: flex;
		flex-wrap: wrap;
		padding: 20rpx;
		gap: 20rpx;

		.filter-btn {
			padding: 12rpx 30rpx;
			border-radius: 40rpx;
			background: #d9d9d9;
			color: #666;

			&.active {
				background: #ed80a0;
				color: #333;
			}
		}
	}

	.safe-box-btn {
		position: fixed;
		right: 0rpx;
		top: 50%;
		transform: translateY(-50%);
		width: 60rpx;
		height: 80rpx;
		background: #ed80a0;
		border-radius: 60rpx 0rpx 0 60rpx;
		display: flex;
		flex-direction: column;
		padding-left: 20rpx;
		padding-top: 10rpx;
		align-items: center;
		z-index: 100;

		

		.count {
			color: #fff;
			font-size: 28rpx;
		}
	}

	.shipping-tips {
		margin: 20rpx;
		margin-bottom: 0px;
		width: 90%;
		height: 50rpx;
		background-size: cover;
		/* 或使用 contain 根据需求 */
		background-position: center;
		background-repeat: no-repeat;
		background-image: url('data:image/png;base64,');
	}

	.product-list {
		padding: 20rpx 0rpx;

		.product-item {
			display: flex;
			background: #fff;
			border-top-right-radius: 16rpx;
			border-top-left-radius: 16rpx;
			margin: 0 20rpx;
			padding: 20rpx;
			padding-bottom: 0px;

			.product-img {
				width: 200rpx;
				height: 200rpx;
				border-radius: 12rpx;
				margin-right: 20rpx;
				flex-shrink: 0;
			}

			.product-info {
				flex-shrink: 0;

				.top-section {
					font-size: 32rpx;
					width: 460rpx;
					font-weight: bold;
					white-space: nowrap; /* 禁止文本换行 */
					overflow: hidden; /* 隐藏超出范围的内容 */
					text-overflow: ellipsis; /* 使用省略号 */
					display: -webkit-box; /* 将对象作为弹性伸缩盒子模型显示 */
					-webkit-box-orient: vertical; /* 垂直排列子元素 */
					-webkit-line-clamp: 2; /* 限制显示的行数为两行 */
					margin-bottom: 15rpx;
				}

				.meta-info {
					display: flex;
					gap: 20rpx;
					font-size: 12px;
					margin-bottom: 15rpx;
					color: #666;

					text {
						display: inline-block;
						padding: 10rpx 25rpx;
						background-color: #d9d9d9;
						text-align: center;
						border-radius: 25rpx;
					}
				}

				.medal-info {
					display: flex;
					justify-content: space-between;
					align-items: center;
					margin-bottom: 15rpx;

					text {
						color: #ff91da;
						font-size: 14px;
					}

					.status-btn {
						padding: 8rpx 20rpx;
						border: 1rpx solid #D4B483;
						border-radius: 10rpx;
						background-color: #ed80a0;
						color: #fff;
						line-height: 50rpx;
						text-align: center;
						width: 150rpx;
						font-weight: bold;
					}
				}


			}
		}

		.bottom-section {
			display: flex;
			justify-content: space-between;
			align-items: center;
			color: #999;
			margin: 0 20rpx;
			background-color: #fff;
			padding: 0 20rpx;
			margin-bottom: 20rpx;
			padding-bottom: 20rpx;
			padding-top: 10rpx;
			border-bottom-left-radius: 16rpx;
			border-bottom-right-radius: 16rpx;

			.donate-btn {
				padding: 8rpx 20rpx;
				background: #000;
				color: #fff;
				border-radius: 20rpx;
				width: 150rpx;
				line-height: 50rpx;
				font-weight: bold;
				text-align: center;
				word-spacing: 4rpx;
			}
			.pink-btn{
				background-color: #ed80a0;
			}
			.lock-icon{
				width: 100rpx;
				height: 50rpx;
				background-image: url('/static/lock.png');
				background-size: cover;
				/* 或使用 contain 根据需求 */
				background-position: center;
				background-repeat: no-repeat;
				margin-right: 15rpx;
				margin-top: 8rpx;
			}
			.right-bottom-wrapper{
				display: inline-flex;
			}
		}

		.bottom-section-position {
			justify-content: flex-end;
		}
	}

	.bottom-action {
		position: fixed;
		bottom: 0;
		width: 100%;
		padding: 20rpx;
		background: #fff;
		border-top: 1rpx solid #eee;
		display: flex;
		align-content: space-between;

		.ship-btn {
			height: 90rpx;
			line-height: 90rpx;
			text-align: center;
			border-radius: 45rpx;
			font-size: 32rpx;
			margin-right: 5%;
			background-size: 60%;
			/* 或使用 contain 根据需求 */
			background-position: center;
			background-repeat: no-repeat;
			background-image: url('data:image/png;base64,');

			&.disabled {
				// background: #ccc;
				color: #666;
			}
		}

		.defaut-btn {
			width: 45%;
		}

		.market-btn {
			/* 关键样式 */
			background-size: 60%;
			/* 或使用 contain 根据需求 */
			background-position: center;
			background-repeat: no-repeat;
			background-image: url('data:image/png;base64,');
		}

	}

	.bottom-action-select {
		position: fixed;
		bottom: 0;
		width: 100%;
		padding: 20rpx;
		background: #fff;
		border-top: 1rpx solid #eee;
		display: flex;
		align-content: space-between;

		.default-btn {
			width: 25%;
			height: 90rpx;
			line-height: 90rpx;
			text-align: center;
			border-radius: 45rpx;
			font-size: 32rpx;
			margin-right: 15rpx;
			background-color: #ed80a0;
			color: #fff;
		}

		.select-product {
			width: 50%;
		}
	}
</style>
<style  lang="scss">
	.safe-box-btn{
		.uni-icons{
			color: #fff !important;
		}
	}
	
</style>