</template>

<script>
	import countdownTimer from './components/countdownTimer.vue';

	import {
		get,
		post
	} from "@/utils/rest-util.js"
	export default {
		components: {
			countdownTimer
		},
		onLoad(param) {
			const {
				userId,
				shareUserId
			} = param;
			this.shareUserId = shareUserId;
			this.userId = uni.getStorageSync('userId');
			if (shareUserId) {
				const shareParams = {
					path: this.$mp.page.route,
					query: param // 包含shareUserId等参数
				}
				uni.setStorageSync('shareParams', JSON.stringify(shareParams));
			}

			// 检查登录状态
			if (!uni.getStorageSync('userId')) {
				uni.redirectTo({
					url: '/pages/login/index'
				});
				return;
			}
			if (this.shareUserId) {
				this.$refs.helppopup.open('bottom');
			}

			this.fetchActivityInfo();
		},
		onShow() {
			this.activityInfo();
		},
		data() {
			return {
				shareUserName: 'xx',
				codeNum: 1,
				allCodeNum: 1,
				shareUserId: '',
				countdownTime: '20天05时58分44秒',
				rules: [],
				imgHeight: '35vh', // 图片区域高度
				contentHeight: 'calc(65vh - 100rpx)', // 计算内容区域高度
				contentOffset: 0,
				status: 1,
				activeInfo: {},
				prizeList: [],
				participants: [],
				imageList: [],
				userCodesList: [],
				userId: '',
				endTime: '', // 从后台获取的结束时间
				activityStatus: true // 从后台获取的活动状态
			}
		},
		computed: {
			contentStyle() {
				return {
					marginTop: `calc(${this.imgHeight} - 80rpx)`,
					paddingTop: '140rpx'
				}
			},
			luckyUser() {
				let num = 0;
				this.prizeList.forEach(item => {
					num += item.productQuantity
				})
				return num
			},
			// 获取前5个参与者
			firstFiveParticipants() {
				return this.participants.slice(0, 5)
			},
			// 获取剩余参与者
			remainingParticipants() {
				return this.participants.slice(5)
			}
		},
		mounted() {
			this.calculateLayout()
		},
		onShareAppMessage() {
			return {
				title: '幸运大抽奖', // 分享标题
				path: '/subActivity/lucky/index?shareId=' + this.userId, // 分享路径（默认当前页面路径）
				imageUrl: this.imageList[0], // 分享图片
				forwardChatType: 0,
				success: (res) => {
					console.log('分享成功', res);
				},
				fail: (err) => {
					console.log('分享失败', err);
				}
			}
		},
		methods: {
			forwarTohelp() {

			},
			closeHelppupop() {
				this.$refs.helppopup.close();
			},
			handHelpCode() {
				const postData = {
					userId: this.shareUserId,
					activityId: this.activeInfo.activityId,
					activityName: this.activeInfo.activityName,
					periodNumber: this.activeInfo.periodNumber,
					helperId: this.userId
				}
				post(`wx/luckyDraw/help`, postData).then(json => {

				})
			},
			changePopup(e) {
				console.log('当前模式：' + e.type + ',状态：' + e.show);
			},
			navToHistory(tab) {
				uni.navigateTo({
					url: `/subActivity/lottery/history?userId=${this.userId}&activityId=${this.activeInfo.activityId}&periodNumber=${this.activeInfo.periodNumber}&tab=${tab}`
				})
			},
			handleMyCodes() {
				uni.navigateTo({
					url: '/subActivity/lucky/luckyCode?userId=' + this.userId
				})
			},
			handleActivity() {
				uni.navigateTo({
					url: '/subActivity/lucky/luckyActivity'
				})
			},
			scrollToRules() {
				uni.pageScrollTo({
					selector: '.rules-section',
					duration: 300
				})
			},
			scrollToCodes() {
				uni.pageScrollTo({
					selector: '.code-section',
					duration: 300
				})
			},
			handleShare() {
				if(!this.activityStatus) {
					uni.showToast({
						title: "活动已结束！",
						icon: "none"
					})
					return;
				}
				
				get(`wx/luckyDraw/userCodes?userId=${this.userId}&activityId=${this.activeInfo.activityId}&periodNumber=${this.activeInfo.periodNumber}`)
					.then(json => {
						const result = json.data?.data;
						this.$refs.userCodespopup.open('bottom');
						this.userCodesList = result || []
					})
			},
			calculateLayout() {
				const query = uni.createSelectorQuery().in(this)
				query.select('.top-section').boundingClientRect(res => {
					this.imgHeight = `${res.height}px`
				}).exec()
			},
			handleScroll(e) {
				const scrollTop = e.detail.scrollTop
				const maxOffset = 100 // 最大上滑距离

				// 计算悬浮偏移量
				this.scrollOffset = Math.min(
					Math.max(scrollTop - this.lastScrollTop, -maxOffset),
					maxOffset
				)

				// 添加阻尼效果
				if (scrollTop <= 0) {
					this.scrollOffset = scrollTop * 0.5
				}

				this.lastScrollTop = scrollTop
			},
			activityInfo() {
				const that = this;
				get('wx/luckyDraw/activity').then(json => {
					const result = json.data?.data;
					that.imageList = result.activityDetailImages || [];
					that.activeInfo = result || {}
					that.rules = result.activityRules?.split?.('\n');
					that.prizeList = result?.prizeList || [];
					that.participantsFun()
				})
			},
			participantsFun() {
				get(`wx/luckyDraw/participants?activityId=${this.activeInfo.activityId}&periodNumber=${this.activeInfo.periodNumber}`)
					.then(json => {
						const result = json.data?.data;
						this.participants = result || []
					})
			},
			handleParticipate() {
				const postData = {
					userId: this.userId,
					activityId: this.activeInfo.activityId
				}
				post(`wx/luckyDraw/joinLottery`, postData).then(json => {
					const result = json.data;
					if (result.errmsg === '成功') {
						this.handleMyCodes()
					}
				})
			},
			async fetchActivityInfo() {
				try {
					// 替换为你的实际API调用
					get('wx/luckyDraw/activity').then(json => {
						const result = json.data?.data;
						this.endTime = result.drawDate
						this.activityStatus = result.isActive
					})


				} catch (error) {
					console.error('获取活动信息失败:', error)
					uni.showToast({
						title: '获取活动信息失败',
						icon: 'none'
					})
				}
			}
		}
	}
</script>

